import React, { createContext, useContext, useEffect, useState } from "react";
import io from "socket.io-client";

const SocketContext = createContext();

export const useSocket = () => {
  const context = useContext(SocketContext);
  if (!context) {
    throw new Error("useSocket must be used within a SocketProvider");
  }
  return context;
};

export const SocketProvider = ({ children }) => {
  const [socket, setSocket] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [user, setUser] = useState(() => {
    // Try to restore user from localStorage
    const savedUser = localStorage.getItem('chatapp-user');
    return savedUser ? JSON.parse(savedUser) : null;
  });

  useEffect(() => {
    // Initialize socket connection
    const newSocket = io(
      process.env.REACT_APP_BACKEND_URL || "http://localhost:5000",
      {
        transports: ["websocket"],
        autoConnect: false,
      }
    );

    // Connection events
    newSocket.on("connect", () => {
      console.log("Connected to server");
      setIsConnected(true);
    });

    newSocket.on("disconnect", () => {
      console.log("Disconnected from server");
      setIsConnected(false);
    });

    newSocket.on("connect_error", (error) => {
      console.error("Connection error:", error);
      setIsConnected(false);
    });

    // User events
    newSocket.on("user-joined", (userData) => {
      console.log("User joined:", userData);
      setUser(userData);
      localStorage.setItem('chatapp-user', JSON.stringify(userData));
    });

    newSocket.on("error", (error) => {
      console.error("Socket error:", error);
    });

    setSocket(newSocket);

    return () => {
      newSocket.close();
    };
  }, []);

  // Connect to server
  const connect = () => {
    if (socket && !isConnected) {
      socket.connect();
    }
  };

  // Disconnect from server
  const disconnect = () => {
    if (socket && isConnected) {
      socket.disconnect();
    }
  };

  // Logout user
  const logout = () => {
    setUser(null);
    localStorage.removeItem('chatapp-user');
    if (socket && isConnected) {
      socket.disconnect();
    }
  };

  // Join user with username
  const joinUser = (username, avatar) => {
    return new Promise((resolve, reject) => {
      if (socket && isConnected) {
        socket.emit("join-user", { username, avatar });

        const timeout = setTimeout(() => {
          socket.off("user-joined");
          socket.off("join-user-error");
          reject(new Error("Timeout joining user"));
        }, 5000);

        socket.once("user-joined", (userData) => {
          clearTimeout(timeout);
          setUser(userData);
          localStorage.setItem('chatapp-user', JSON.stringify(userData));
          resolve(userData);
        });

        socket.once("join-user-error", (error) => {
          clearTimeout(timeout);
          reject(new Error(error.message || "Failed to join"));
        });
      } else {
        reject(new Error("Socket not connected"));
      }
    });
  };

  // Join a room
  const joinRoom = (roomId) => {
    return new Promise((resolve, reject) => {
      if (socket && isConnected) {
        socket.emit("join-room", { roomId });

        const timeout = setTimeout(() => {
          socket.off("room-joined");
          socket.off("join-room-error");
          reject(new Error("Timeout joining room"));
        }, 5000);

        socket.once("room-joined", (roomData) => {
          clearTimeout(timeout);
          resolve(roomData);
        });

        socket.once("join-room-error", (error) => {
          clearTimeout(timeout);
          reject(new Error(error.message || "Failed to join room"));
        });
      } else {
        reject(new Error("Socket not connected"));
      }
    });
  };

  // Leave room
  const leaveRoom = () => {
    if (socket && isConnected) {
      socket.emit("leave-room");
    }
  };

  // Send chat message
  const sendMessage = (content) => {
    if (socket && isConnected) {
      console.log('ðŸ“¨ Frontend: Sending message:', content);
      socket.emit("chat-message", { content });
    } else {
      console.log('âŒ Frontend: Cannot send message - socket connected:', isConnected);
    }
  };

  // Start typing indicator
  const startTyping = () => {
    if (socket && isConnected) {
      socket.emit("typing-start");
    }
  };

  // Stop typing indicator
  const stopTyping = () => {
    if (socket && isConnected) {
      socket.emit("typing-stop");
    }
  };

  // Get room status
  const getRoomStatus = () => {
    return new Promise((resolve, reject) => {
      if (socket && isConnected) {
        socket.emit("get-room-status");

        // Listen for the response
        const timeout = setTimeout(() => {
          socket.off("room-status-response");
          reject(new Error("Timeout waiting for room status"));
        }, 5000);

        socket.once("room-status-response", (rooms) => {
          clearTimeout(timeout);
          resolve(rooms || []);
        });
      } else {
        reject(new Error("Socket not connected"));
      }
    });
  };

  const value = {
    socket,
    isConnected,
    user,
    connect,
    disconnect,
    logout,
    joinUser,
    joinRoom,
    leaveRoom,
    sendMessage,
    startTyping,
    stopTyping,
    getRoomStatus,
  };

  return (
    <SocketContext.Provider value={value}>{children}</SocketContext.Provider>
  );
};
